import numpy as np
def heuristics_v2(el: tuple[int, ...], n: int, w: int) -> float:
    # Calculate the weight of each element by taking into consideration the total weight and the number of elements.
    element_weight = w / len(el)
    # We want to add elements that have the highest priority, which is calculated using the element weight and the number of elements.
    return element_weight * (len(el) + 1)
