import numpy as np
def heuristics_v2(el, n, w, *, cap_per_set=4, cap_per_set_abs=2000):
    size = len(el)
    n_sets = min(size, cap_per_set)
    for p in range(size):
        if el[p] < n:
            for k in range(2 * n_sets):
                min_val = sum(el[: p + 1]) + k * (w - p)
                if min_val - n_sets * w > size - 1 and min_val - n * w < size - 1:
                    priority = 1.0
                    break
        else:
            for k in range(2 * n_sets - 1, -1, -1):
                min_val = sum(el[: p + 1]) + k * w - p
                if min_val - n_sets * w > size - 1 and min_val - n * w < size - 1:
                    priority = 1.0
                    break
    else:
        priority = 0.0

    return priority
